                % ---------- Compute the local angle of attack (α) ----------
                 s.alpha = s.phi - s.theta; % in [rad]
                s.alpha_deg = rad2deg( s.alpha ); % in [deg]

                % # STEP (8):
                % ---------- Compute the Lift and Drag Coefficients ----------
                s.Cl = zeros(1, length(s.RNodes));
                s.Cd = zeros(1, length(s.RNodes));     
                
                for k = 1:length(s.N_be)
                    aerofoil = s.DistributedBlade{k, 6}; 
                    
                    % Adjust s.alpha_deg to the range -180 to 180 degrees
                    s.alpha_deg(k) = mod(s.alpha_deg(k) + 180, 360) - 180;                

                    % ---------- Interpolation of Cl(α) and Cd(α) ----------
                    switch aerofoil
                        case 'Cylinder1'
                            s.alpha_used(k) = s.alpha_deg(k);
                            s.Cl(k) = 0; % Always 0 for Cylinder1
                            s.Cd(k) = 0.5; % Always 0.5 for Cylinder1                           
                        case 'Cylinder2'
                            s.alpha_used(k) = s.alpha_deg(k);
                            s.Cl(k) = 0; % Always 0 for Cylinder2
                            s.Cd(k) = 0.35; % Always 0.5 for Cylinder1
                        case 'DU40_A17'                                              
                            s.Cl(k) = interp1(s.Airfoil_DU40_A17.alpha, s.Airfoil_DU40_A17.Cl, s.alpha_deg(k) );
                            s.Cd(k) = interp1(s.Airfoil_DU40_A17.alpha, s.Airfoil_DU40_A17.Cd, s.alpha_deg(k) );
                        case 'DU35_A17'
                            s.Cl(k) = interp1(s.Airfoil_DU35_A17.alpha, s.Airfoil_DU35_A17.Cl, s.alpha_deg(k) );
                            s.Cd(k) = interp1(s.Airfoil_DU35_A17.alpha, s.Airfoil_DU35_A17.Cd, s.alpha_deg(k) );
                        case 'DU30_A17'
                            s.alpha_deg(k) = max(min(s.alpha_deg(k), 9), -9); % Limit angle of attack to stall limits (±9 degrees), according to notes in DU30_A17 airfoil file.
                            s.Cl(k) = interp1(s.Airfoil_DU30_A17.alpha, s.Airfoil_DU30_A17.Cl, s.alpha_deg(k) );
                            s.Cd(k) = interp1(s.Airfoil_DU30_A17.alpha, s.Airfoil_DU30_A17.Cd, s.alpha_deg(k) );   
                        case 'DU25_A17'
                            s.alpha_deg(k) = max(min(s.alpha_deg(k), 8.5), -8.5); % Limit angle of attack to stall limits (±8.5 degrees), according to notes in DU25_A17 airfoil file.
                            s.Cl(k) = interp1(s.Airfoil_DU25_A17.alpha, s.Airfoil_DU25_A17.Cl, s.alpha_deg(k) );
                            s.Cd(k) = interp1(s.Airfoil_DU25_A17.alpha, s.Airfoil_DU25_A17.Cd, s.alpha_deg(k) );
                        case 'DU21_A17'
                            s.alpha_deg(k) = max(min(s.alpha_deg(k), 8.0), -8.0); % Limit angle of attack to stall limits (±8.0 degrees), according to notes in DU21_A17 airfoil file.
                            s.Cl(k) = interp1(s.Airfoil_DU21_A17.alpha, s.Airfoil_DU21_A17.Cl, s.alpha_deg(k) );
                            s.Cd(k) = interp1(s.Airfoil_DU21_A17.alpha, s.Airfoil_DU21_A17.Cd, s.alpha_deg(k) );
                        case 'NACA64_A17'
                            s.alpha_deg(k) = max(min(s.alpha_deg(k), 9), -9); % Limit angle of attack to stall limits (±9 degrees), according to notes in NACA64_A17 airfoil file.
                            s.Cl(k) = interp1(s.Airfoil_NACA64_A17.alpha, s.Airfoil_NACA64_A17.Cl, s.alpha_deg(k) );
                            s.Cd(k) = interp1(s.Airfoil_NACA64_A17.alpha, s.Airfoil_NACA64_A17.Cd, s.alpha_deg(k) );                       
                        otherwise
                        error('Unknown Airfoil: %s', aerofoil);
                    end % end switch aerofoil

                    who.AngleAttack = 'local angle of attack';
                    s.AngleAttack(i, j, k) = s.alpha_deg(k);
                    who.C_lift = 'local Lift Coefficient';
                    s.C_lift(i, j, k) = s.Cl(k);
                    who.C_drag = 'local Drag Coefficient';
                    s.C_drag(i, j, k) = s.Cd(k);                  

                end % ende for k = 1:length(s.N_be)

            % # STEP (9):            
            % ----- Compute Normal and Tangential Force Coefficients Cn and Ct -----
            s.Cn = s.Cl .* cos(s.phi) + s.Cd .* sin(s.phi);
            s.Ct = s.Cl .* sin(s.phi) - s.Cd .* cos(s.phi);

            % Save previous values ​​of a and a'
            if s.Option02f5 == 2
                s.a0 = s.a; 
                s.aprime0 = s.aprime;
            end               
                    
            % # STEP (10):
            % ----- Compute Axial and Tangential Induction Factors a and a' -----
            s.a_new = 1./ ( ( (4 .* sin(s.phi).^2)./(s.sigma .* s.Cn) ) + 1 );           
            s.aprime_new = 1./ ( ( (4 .* sin(s.phi) .* cos(s.phi)) ./ (s.sigma .* s.Ct) ) - 1 );
            
            % ----- Limits -----
            who.a_max = 'Maximum Axial induction factor (a).';       
            s.a_max = 1/3;            
            s.a_new = max(min( real(s.a_new), s.a_max ), 0);
            who.aprime_max = 'Maximum Tangential induction factor (a´).';
            s.aprime_max = ( s.a_max*(1 - s.a_max) ) ./ ( (s.Lambda_Tab(j)^2) .* s.mu.^2 );
            s.aprime_new = max(min( real(s.aprime_new), s.aprime_max), 0);

            % Apply weighted correction for convergence stability
            if s.Option02f5 == 2
                s.a = s.a0 + s.wt * (s.a_new - s.a0);
                s.aprime = s.aprime0 + s.wt * (s.aprime_new - s.aprime0);
            end   
                
            % # STEP (11):            
            % ---------- Checking convergence ----------
            if s.Option02f5 == 2
                s.da = abs(s.a0 - s.a); 
                s.dap = abs(s.aprime0 - s.aprime); 
                if all( s.da < s.tol_bem ) && all( s.dap < s.tol_bem ) 
                    convergencia = true;
                end                    
            else 
                if max(abs(s.a - s.a_new)) < s.tol_bem && max(abs(s.aprime - s.aprime_new)) < s.tol_bem
                    convergencia = true;
                end  
            end  

            % # STEP (12):  
            % Update a and a' for the next iteration
            s.a = s.a_new;
            s.aprime = s.aprime_new;
            who.AxialInduction = 'Axial induction factor (a).';
            s.AxialInduction(i, j, :) = s.a;
            who.AxialTangential = 'Tangential induction factor (a´).';
            s.AxialTangential(i, j, :) = s.aprime;  
            
            % Prevent infinite loop 
            if s.num_iterations > 1000 
                error('O processo de convergência não completou em 1000 iterações'); 
            end

            end % end while ~convergencia
        % ##
        % ## Convergência alcançada, finalizando iterações


        % # STEP (13):
        % ----- Compute the resulting vector of Relative Wind Speed -------
                 % Resulting vector:  Vws_rel ^2 = Vn^2 + Vt^2)
        s.Vws_rel = sqrt(  (s.Vws(j).*(1 - s.a) ).^2 + ( (s.OmegaR_bem(j).* s.Rrd_local).*(1 + s.aprime) ).^2  );


        % # STEP (14):
        % ----- Compute the Normal and Tangential Forces -------        
        s.dL = 0.5 * s.rho_ws * s.Vws_rel.^2 .* s.chord .* s.Cl; % Normal Force expressed in terms of the differential lift force.
        s.dFL(i, j, :) = s.dL;
        s.dD = 0.5 * s.rho_ws * s.Vws_rel.^2 .* s.chord .* s.Cd; % Tangential Force expressed in terms of the differential drag force.
        s.dFD(i, j, :) = s.dD;

        % # STEP (15):
        % ---------- Calculating Power, Thrust and Torque Coefficients ----------
        who.AerodynamicPerformance = 'Aerodynamic performance of the NREL-5MW reference wind turbine.';
        who.CPP = 'Power coefficient, in [dimensionless].';
        s.CPP(i, j) = ( 8/(s.Lambda_Tab(j)^2) ) * trapz(s.mu, (s.mu ./ s.Rrd) .* s.Ct); % Eq(3.87) Burton(2001)
        who.CTT = 'Thrust coefficient (CT), in [dimensionless].';
        s.CTT(i, j) = ( 8/(s.Lambda_Tab(j)^2) ) * trapz(s.mu, (s.mu ./ s.Rrd) .* s.Cn);
        who.CQQ = 'Torque coefficient (CQ), in [dimensionless].';
        s.CQQ(i, j) = s.CPP(i, j) / s.Lambda_Tab(j);


        % # STEP (16):
        % ---------- Compute the Tip Loss Factor (F) by Ludwig Prandtl. ----------
        f_tlf = ( s.Nb.*(s.Rrd - s.Rrd_local) ) ./ ( 2.* s.Rrd_local.* sin(s.phi) );
        ff_tlf = exp(1) .^ (-f_tlf);
        s.ftlf = (2/pi) .* ( 1./cos( ff_tlf ) );
        who.TipLossFactor = 'Tip Loss Factor (F)';
        s.TipLossFactor(i, j, :) = s.ftlf;


        % # STEP (17):   
        % ----- Correct Axial and Tangential Induction Factors a and a' with Tip Loss Factor -----
        s.a_c = 1./ ( ( (4 .* s.ftlf .* sin(s.phi).^2)./(s.sigma .* s.Cn) ) + 1 );
        s.aprime_c = 1./ ( ( (4 .* s.ftlf .* sin(s.phi) .* cos(s.phi)) ./ (s.sigma .* s.Ct) ) - 1 );
        
        who.AxialInduction_Tiploss = 'Axial induction factor corrected with Tip Loss Factor (ac).';
        s.AxialInduction_Tiploss(i, j, :) = s.a_c;
        who.AxialTangential_Tiploss = 'Tangential induction factor corrected with Tip Loss Factor (ac´).';
        s.AxialTangential_Tiploss(i, j, :) = s.aprime_c;         


        % # STEP (18):   
        % ----- Correct Power, Thrust and Torque Coefficients  -----
        who.CP_tiplost = 'Power coefficient including Blade Tip Loss, in [dimensionless].';
        s.CP_tiplost(i, j) = ( 8/(s.Lambda_Tab(j)^2) ) * trapz(s.mu, (s.mu ./ s.Rrd) .* s.Ct .*s.ftlf); 
        who.CT_tiplost = 'Thrust coefficient (CT) including Blade Tip Loss, in [dimensionless].';
        s.CT_tiplost(i, j) = ( 8/(s.Lambda_Tab(j)^2) ) * trapz(s.mu, (s.mu ./ s.Rrd) .* s.Cn .*s.ftlf);
        who.CQ_tiplost = 'Torque coefficient (CQ) including Blade Tip Loss, in [dimensionless].';
        s.CQ_tiplost(i, j) = s.CP(i, j) / s.Lambda_Tab(j);

        if s.Option03f3 == 2 || s.Option03f3 == 3 % Consider Blade Dynamics
            ConsiderTipLost = 1;
        end
        if s.Option03f5 == 2 && ConsiderTipLost == 1
            who.CP = 'Power coefficient, in [dimensionless].';
            s.CP(i, j) = s.CP_tiplost(i, j);
            who.CT = 'Thrust coefficient (CT), in [dimensionless].';
            s.CT(i, j) = s.CT_tiplost(i, j);
            who.CQ = 'Torque coefficient (CQ), in [dimensionless].';
            s.CQ(i, j) = s.CQ_tiplost(i, j);
        else
            who.CP = 'Power coefficient, in [dimensionless].';
            s.CP(i, j) = s.CPP(i, j);
            who.CT = 'Thrust coefficient (CT), in [dimensionless].';
            s.CT(i, j) = s.CTT(i, j);
            who.CQ = 'Torque coefficient (CQ), in [dimensionless].';
            s.CQ(i, j) = s.CQQ(i, j);
        end

        % # STEP (19):   
        % ----- Compute the Span-wise Variation of Power Extraction  -----
        who.RCp_Dr = 'Span-wise Variation of Power Extraction R*(dCp/dr) .';        
        s.RCp_Dr(i, j, :) = 8 .*( s.Rrd_local./s.Rrd) .* s.a_c .* (1 - s.a_c) .* (1 - s.aprime_c);

        % Span-wise Variation of Power Extraction:
        % dCp = 8*mu*a*(1 - a)*(1 - a')*dmu; (equation 3.73 Burton)
        % mu = r/R >> (deriving mu) >> dmu = (1/R)*dr (apply above)
        %dCp = 8*(r/R)*a*(1 - a)*(1 - a')*(1/R)*dr >>>
        % R*(dCp/dr) = 8*(r/R)*a*(1 - a)*(1 - a')  >>> (Span-wise Variation)

        % The "R*(dCp/dr)" value is used to calculate the Weighted Average of the 
        % Wind Field, considering tip loss, as was done in
        % Soltani/Knudsen(2013) and apply to obtain Effective Wind Speed.

        end  % end for j
    end % end for i


    % Organizing output results 
    BEM_Theory.Beta_Tab = s.Beta_Tab; BEM_Theory.Lambda_Tab = s.Lambda_Tab; BEM_Theory.CP_Tab = s.CP_Tab; BEM_Theory.CT_Tab = s.CT_Tab; BEM_Theory.CQ_Tab = s.CQ_Tab; 
    BEM_Theory.CP_tiplost = s.CP_tiplost; BEM_Theory.CT_tiplost = s.CT_tiplost; BEM_Theory.CQ_tiplost = s.CQ_tiplost;       
    BEM_Theory.CPP = s.CPP; BEM_Theory.CTT = s.CTT; BEM_Theory.CQQ = s.CQQ;    
    BEM_Theory.BladeData = 'Aerodynamic Properties of a Reference Wind Turbine Blade.';    
    BEM_Theory.N_blades = s.Nb; BEM_Theory.RNodes = s.N_be; BEM_Theory.DRNodes = s.Rrd_local; BEM_Theory.mu = s.mu; BEM_Theory.Chord= s.chord; BEM_Theory.Theta_b = s.Theta_b;  
    BEM_Theory.BemOperationCond = 'BEM Theory Operating Conditions.'; 
    BEM_Theory.Beta_Tab = s.Beta_Tab; BEM_Theory.Lambda_Tab = s.Lambda_Tab; BEM_Theory.rho_ws = s.rho_ws; BEM_Theory.Vws = s.Vws; BEM_Theory.OmegaR_bem = s.OmegaR_bem;
    BEM_Theory.tol_bem = s.tol_bem; BEM_Theory.DistributedBlade = s.DistributedBlade;
    BEM_Theory.BEMTheoryAlgorithm = 'BEM Theory Algorithm.';
    BEM_Theory.BEMTheoryAlgorithm = BEM_Theory.BEMTheoryAlgorithm; BEM_Theory.a_max = s.a_max; BEM_Theory.dFD = s.dFD; BEM_Theory.s.dFL = s.dFL; BEM_Theory.FlowAngle = s.FlowAngle; BEM_Theory.C_drag = C_drag; BEM_Theory.C_lift = C_lift; BEM_Theory.AngleAttack = s.AngleAttack; BEM_Theory.TipLossFactor = s.TipLossFactor; BEM_Theory.AxialInduction = s.AxialInduction; BEM_Theory.AxialTangential = s.AxialTangential; BEM_Theory.RCp_Dr = s.RCp_Dr;


    % Assign value to variable in specified workspace
    assignin('base', 's', s);
    assignin('base', 'who', who);
    assignin('base', 'AerodynamicModels', BEM_Theory);


    % Return to WindTurbineData_NREL5MW('logical_instance_08');


