        plot(lambda_optx, Cp_optx, 'k*', lambda_x(1,:), Cp_xy(1,:))
        xlabel('$\lambda$', 'Interpreter', 'latex')
        xlim([(min(s.Lambda_Tab) - 0.5) (max(s.Lambda_Tab) + 0.5)])
        ylabel('$C_{p}$', 'Interpreter', 'latex')
        ylim([0 (max(max(s.CP_Tab)) + 0.025)])

        % Armazena as entradas da legenda
