    figure
    quiver3(s.Lambda_Tab,s.Beta_Tab,s.CP_Tab',s.Grad_CtLambda',s.Grad_CtBeta',s.graddT','b')
    xlabel('${\frac{ {\partial{C_{T}}} }{ {\partial{\beta}} } }$','Interpreter','latex')
    ylabel('${\frac{ {\partial{C_{T}}} }{ {\partial{\lambda}} } }$','Interpreter','latex')
