    who.Heave = 'Position displacement in Heave or Linear Vertical Movement (up/down), in [m].';
    AerodynamicModels.Heave(it) = s.Heave ; 
    who.Heave_dot = 'Velocity in Heave or Linear Transverse Movement (side to side or port-starboard), in [m/s].';
    AerodynamicModels.Heave_dot(it) = s.Heave_dot ; 
    who.Heave_Ddot = 'Acceleration in Heave or Linear Transverse Movement (side to side or port-starboard), in [m/s^2].';
    AerodynamicModels.Heave_Ddot(it) = s.Heave_Ddot ;
    who.RollAngle = 'Roll angle or pitch rotation of the offshore wind turbine structure around its longitudinal/X axis (front-back or bow-stern), in [rad].';
    AerodynamicModels.RollAngle(it) = s.RollAngle ; 
    who.RollAngle_dot = 'Roll angular velocity or pitch rotation angular velocity of the offshore wind turbine structure around its longitudinal/X axis (front-back or bow-stern), in [rad/s].';
    AerodynamicModels.RollAngle_dot(it)  = s.RollAngle_dot ; 
    who.PitchAngle = 'Pitch angle or up/down rotation of the offshore wind turbine structure about its transverse/Y axis (side-to-side or port-starboard), in [rad].';
    AerodynamicModels.PitchAngle(it) = s.PitchAngle ; 
    who.PitchAngle_dot = 'Pitch angular velocity or up/down rotation angular velocity of the offshore wind turbine structure about its transverse/Y axis (side-to-side or port-starboard), in [rad/s].';
    AerodynamicModels.PitchAngle_dot(it) = s.PitchAngle_dot ;  
    who.YawAngle = 'Yaw angle or rotation of the offshore wind turbine structure around its vertical/Z axis, in [rad].';
    AerodynamicModels.YawAngle = s.YawAngle ; 
    who.YawAngle_dot = 'Yaw angular velocity or angular velocity of the rotation of the offshore wind turbine structure around its vertical/Z axis, in [rad/s].';
    AerodynamicModels.YawAngle_dot(it)  = s.YawAngle_dot ;
    if (s.Option02f2 > 1)  
        % Offshore wind turbine
        who.MomentArmPlatform = 'Moment arm from platform to tower-top, in [m].';
        AerodynamicModels.MomentArmPlatform = s.MomentArmPlatform;
    end



           %######### MOTIONS THAT AFFECT WIND INFLOW #########%


    % --- Relative movement of the blades (flap-wise motion) -------    
    who.zetaB_dot = 'Angular velocity out of the plane of rotation, in [rad/s]';
    AerodynamicModels.zetaB_dot(it) = s.zetaB_dot ;
    who.VCsi_blades = 'Blade-tip velocity component affecting wind inflow due to flap-wise dynamics, in [m/s].';
    if s.Option05f3 == 1 && s.Option07f6 == 1
        % Consider the BLADE DYNAMICS (flap-wise blade bending)
        % And choose Add the effect of blade vibration on the effective wind speed
        s.VCsi_blades = s.zetaB_dot ;
    else
        s.VCsi_blades = 0;
        %
    end

    % ---------- Relative movement of the top of the tower ----------
    who.Xt_dot = 'Tower-top velocity in the Fore–Aft direction, in [m/s].';
    AerodynamicModels.Xt_dot(it) = s.Xt_dot;
    who.VCsi_tower = 'Longitudinal velocity of the tower-top (including dynamic or kinematic motion), representing the structural contribution to wind inflow distortion, in [m/s].';
    if (s.Option04f3 == 1) || ( s.Option02f2 > 1 )
        % Consider the TOWER DYNAMICS (fore-aft movement) OR the Kinematics 
        % of the movement of the top of the tower.
        s.VCsi_tower = s.Xt_dot ;
        %
    else
        % Onshore Wind Turbine and NOT consider the TOWER DYNAMICS
        s.VCsi_tower = 0;
        %
    end

    % Note: s.Xt_dot is used for both VCsi_offshore and VCsi_tower 
    % to ensure traceability of structural contributions.

    who.VCsi_offshore = 'Offshore platform motions that affect wind speed, in [m/s].';
    if (s.Option02f2 > 1)  
        % Offshore wind turbine: Global movement (platform + tower coupled or vibrating)
        s.VCsi_offshore = s.Xt_dot;
        %
    else
        % Onshore wind turbine
        s.VCsi_offshore = 0;
    end


    who.VCsi= 'Rotor Assembly Movements that affect Wind Inflow, in [m/s].';
    if (s.Option02f2 > 1)
        % Consider the OFFSHORE WIND TURBINE
        s.VCsi = s.VCsi_offshore + s.VCsi_blades ;
    else
        % Consider the ONSHORE WIND TURBINE        
        s.VCsi = s.VCsi_tower + s.VCsi_blades ;
        %
    end

   
    % ------ ### Actual Wind speed at hub height ### ----------      
    who.WindSped_hubWT = 'Wind speed at hub height, with interaction with the wind turbine, in [m/s].';
    s.WindSped_hubWT = interp1(s.Time_ws(1:end-1), s.WindSpeed_Hub, t) ;
    s.WindSped_hubWT = s.WindSped_hubWT - s.Xt_dot ;
    s.WindSped_hubWT = max( s.WindSped_hubWT, 0.1 ) ;
       

    % ------ ### Actual Effective Wind Speed (full signal considered) ### ----------  
    if s.Option06f6 == 1
        who.Uews_ref = 'Effective Wind Speed ​​with only spatial effect and without relative motion effects, in [m/s].';        
        s.Uews_ref = interp1(s.Time_ws(1:end-1), s.Uews_sp , t) ;

        who.Uews = 'Effective Wind Speed ​​with spatial, rotational and without relative motion effects, in [m/s].';
        s.Uews = interp1(s.Time_ws(1:end-1), s.Uews_rt , t) ;

        who.Vews = 'Effective Wind Speed ​​with spatial, rotational and relative motion effects, in [m/s].';        
        s.Vews = s.Uews - s.VCsi; 
    else
        who.Uews_ref = 'Effective Wind Speed ​​with only spatial effect and without relative motion effects, in [m/s].';        
        s.Uews_ref = interp1(s.Time_ws(1:end-1), s.Uews_sp , t) ;

        who.Uews = 'Effective Wind Speed ​​with only spatial effect and without relative motion effects, in [m/s].';        
        s.Uews = interp1(s.Time_ws(1:end-1), s.Uews_sp , t) ;
  
        who.Vews = 'Effective Wind Speed ​​with spatial and relative motion effects, in [m/s].';          
        s.Vews = s.Uews - s.VCsi;
        
    end   
    s.Vews = max( s.Vews, 0.1 ) ;    
           % NOTE! In option "s.Option04f6 == 1" consider Tower Shadonw
           % effect and for wind signals generated in this code, the 
           % effect of Wind Shear as a function of height is already
           % included.

    
    % Assign value to variable in specified workspace
    assignin('base', 's', s);
    assignin('base', 'who', who);
    assignin('base', 'AerodynamicModels', AerodynamicModels);


    % Return to "WindTurbine('logical_instance_04')"
    

elseif strcmp(action, 'logical_instance_06')
%==================== LOGICAL INSTANCE 06 ====================
%=============================================================    
    % AERODYNAMIC MODEL: APPLICATION OF LOADS ON THE ROTOR (ONLINE):   
    % Purpose of this Logical Instance: to calculate the aerodynamic loads
    % such as aerodynamic torque, thrust and aerodynamic power. In addition,
    % the respective operating coefficients, rotor speed and the Tip-Speed-Ratio.


    % ---------- Defining Tip-speed ratio (TSR or lambda) ----------
    who.Lambda = 'Tip-speed ratio (TSR) at the current operating point';
    s.Lambda = min(max(((s.Rrd*s.OmegaR)/s.Vews), s.Lambda_min), s.Lambda_max); 

    % ---------- Defining Power coefficient (Cp) ----------
    who.Cp = 'Power coefficient at the current operating point';
    s.Cp = min(max(interp2(s.Lambda_Tab, s.Beta_Tab, s.CP_Tab', s.Lambda, s.Beta), 0), s.CP_max); 

    % ---------- Defining Aerodynamic torque (Ta) ----------
    who.Ta = 'Aerodynamic torque, in [N.m]';
    s.Ta = 0.5*s.rho*pi*s.Rrd^3*s.Cp*(1/s.Lambda)*s.Vews^2; 

    % ---------- Defining Aerodynamic power (Pa) ----------
    who.Pa = 'Aerodynamic power, in [W]';
    s.Pa = 0.5* s.rho*pi*s.Rrd^2*s.Cp*s.Vews^3; 

    % ---------- Defining Thrust coefficient (Ct) ----------
    who.Ct = 'Thrust coefficient at the current operating point';
    s.Ct = min(max(interp2(s.Lambda_Tab, s.Beta_Tab, s.CT_Tab', s.Lambda, s.Beta), 0), s.CT_max); 
    
    % ---------- Defining Aerodynamic thrust (Fa) ----------
    who.Fa = 'Aerodynamic thrust, in [N]';
    s.Fa = 0.5*s.rho*pi*s.Rrd^2*s.Ct*s.Vews^2; 

    % Assign value to variable in specified workspace
    assignin('base', 's', s);
    assignin('base', 'who', who);
    assignin('base', 'AerodynamicModels', AerodynamicModels);

    
    % Return to "WindTurbine('logical_instance_07')"


elseif strcmp(action, 'logical_instance_07')
%==================== LOGICAL INSTANCE 07 ====================
%=============================================================    
    % SAVE CURRENT VALUE (ONLINE):   
    % Purpose of this Logical Instance: to save current values, based on 
    % the last integration step taken.

    % Organizing output results


    % ---------- LOGICAL INSTANCE 05  ----------
    AerodynamicModels.VCsi_tower(it) = s.VCsi_tower; 
    AerodynamicModels.VCsi_blades(it) = s.VCsi_blades;   
    AerodynamicModels.VCsi_offshore(it) = s.VCsi_offshore;   
    AerodynamicModels.VCsi(it) = s.VCsi;
    AerodynamicModels.Uews(it) = s.Uews;
    AerodynamicModels.Vews(it) = s.Vews; 
    AerodynamicModels.Uews_ref(it) = s.Uews_ref;
    AerodynamicModels.WindSped_hubWT(it) = s.WindSped_hubWT ;


    % ---------- LOGICAL INSTANCE 06  ----------
    AerodynamicModels.Lambda(it) = s.Lambda; AerodynamicModels.Cp(it) = s.Cp; AerodynamicModels.Ta(it) = s.Ta; AerodynamicModels.Pa(it) = s.Pa; AerodynamicModels.Ct(it) = s.Ct; AerodynamicModels.Fa(it) = s.Fa;
    %


    % Returns to " EnviromentSimulation('logical_instance_03')"     


elseif strcmp(action, 'logical_instance_08')
%==================== LOGICAL INSTANCE 08 ====================
%=============================================================    
    % PLOT AERODYNAMICS MODEL RESULTS (OFFLINE):   
    % Purpose of this Logical Instance: to plot the results related to the
    % aerodynamics model results and develop any other calculations, tables and data to 
    % support the analysis of the results.
   

    % ---------- Plot Actual Effective Wind Speed ----------  
    figure()     
    plot(s.Time,s.Vews)
    xlabel('$V_{wind}$', 'Interpreter', 'latex')
    xlim([0 max(s.Time)])
    ylabel('Vews [m/s]', 'Interpreter', 'latex')
    ylim([0.95*min(s.Vews) 1.05*max(s.Vews)])        
    legend('Actual Effective Wind Speed [m/s]', 'Interpreter', 'latex')
    title('Actual effective wind speed over time.')
    %    


    % ---------- Plot Actual Aerodynamic Torque (Ta) ----------
    figure()     
    plot(s.Time,s.Ta)
    xlabel('t [seg]', 'Interpreter', 'latex')
    xlim([0 max(s.Time)])
    ylabel('$T_{a}$ [N.m]', 'Interpreter', 'latex')
    ylim([0.95*min(s.Ta) 1.05*max(s.Ta)])        
    legend('Actual Aerodynamic Torque($T_{a}$)', 'Interpreter', 'latex')
    title('Actual Aerodynamic Torque over time.', 'Interpreter', 'latex')  
    %


    % ---------- Plot Actual Aerodynamic Power (Pa) ----------
    if s.Option05f5 == 3
        figure()     
        plot(s.Time,s.Pa)
        xlabel('t [seg]', 'Interpreter', 'latex')
        xlim([0 max(s.Time)])
        ylabel('Pa [W]', 'Interpreter', 'latex')
        ylim([0.95*min(s.Pa) 1.05*max(s.Pa)])        
        legend('Actual Aerodynamic Power ($P_{a}$)', 'Interpreter', 'latex')
        title('Actual Aerodynamic Power over time.', 'Interpreter', 'latex') 
        %

        figure()     
        plot(s.Time,s.Cp)
        xlabel('t [seg]', 'Interpreter', 'latex')
        xlim([0 max(s.Time)])
        ylabel('$C_{p}$', 'Interpreter', 'latex')
        ylim([0.95*min(s.Cp) 1.05*max(s.Cp)])        
        legend('Actual Aerodynamic Power coefficient ($C_{p}$)', 'Interpreter', 'latex')
        title('Actual Aerodynamic Power coefficient over time.', 'Interpreter', 'latex')  
        %
    end


    % ---------- Plot Actual Aerodynamic Thrust (Fa) ----------
    if s.Option05f5 == 3
        figure()     
        plot(s.Time,s.Fa)
        xlabel('t [seg]', 'Interpreter', 'latex')
        xlim([0 max(s.Time)])
        ylabel('Fa [N]', 'Interpreter', 'latex')
        ylim([0.95*min(s.Fa) 1.05*max(s.Fa)])        
        legend('Actual Aerodynamic Thrust ($F_{a}$)', 'Interpreter', 'latex')
        title('Actual Aerodynamic Thrust over time.', 'Interpreter', 'latex')
        %

        figure()     
        plot(s.Time,s.Ct)
        xlabel('t [seg]', 'Interpreter', 'latex')
        xlim([0 max(s.Time)])
        ylabel('$C_{t}$', 'Interpreter', 'latex')
        ylim([0.95*min(s.Ct) 1.05*max(s.Ct)])        
        legend('Actual Aerodynamic Thrust coefficient ($C_{t}$)', 'Interpreter', 'latex')
        title('Actual Aerodynamic Thrust coefficient over time.', 'Interpreter', 'latex')  
        %        
    end


    % ---------- Plot Actual Aerodynamic Thrust (Fa) ----------
    if s.Option05f5 == 3
        figure()     
        plot(s.Time,s.Lambda)
        xlabel('t [seg]', 'Interpreter', 'latex')
        xlim([0 max(s.Time)])
        ylabel('$\lambda$', 'Interpreter', 'latex')
        ylim([0.95*min(s.Lambda) 1.05*max(s.Lambda)])        
        legend('Actual Tip-Speed-Ratio ($\lambda$)', 'Interpreter', 'latex')
        title('Actual Tip-Speed-Ratio over time.', 'Interpreter', 'latex')  
        %
    end
    
    % Return to "WindTurbine('logical_instance_07')"


%=============================================================  
end


% Assign value to variable in specified workspace
assignin('base', 's', s);
assignin('base', 'who', who);
assignin('base', 'AerodynamicModels', BEM_Theory);
assignin('base', 'AerodynamicModels', AerodynamicModels);


% #######################################################################
end